import React, { Component } from 'react'

class CompState extends Component {

    constructor(){
        super()
        this.state={
            message: 'Welcome sir Please click subscribe to get notified'
        }
    }

    changeMessage(){
        this.setState({
            message: 'Thank you for subscribing'
        })
    }

    render() { 
        return ( 
            <div>
                <h1> {this.state.message} </h1>
                <button onClick={()=> this.changeMessage()} >Subscribe</button>
            </div>
         );
    }
}

export default CompState;

/* state is ntg but an object that is privately maintained inside a component */
